	(5, 'Sản - Phụ Khoa', 'Khoa Sản - Phụ Khoa chăm sóc phụ nữ mang thai và các vấn đề phụ khoa.', 'storage/image/thumbnail/departments/6561f929b5f54.jpg', 281, '2023-11-25 13:39:53', '2023-11-25 13:39:53'),
	(6, 'Trị Liệu Thần Kinh Cột Sống', 'Khoa Trị Liệu Thần Kinh Cột Sống chuyên trị liệu các vấn đề về thần kinh và cột sống.', 'storage/image/thumbnail/departments/6561f92a3be27.jpg', 77, '2023-11-25 13:39:54', '2023-11-25 13:39:54'),
	(7, 'Nhi Khoa', 'Khoa Nhi Khoa chăm sóc sức khỏe trẻ em và điều trị các bệnh trẻ em.', 'storage/image/thumbnail/departments/6561f92ae61c9.jpg', 87, '2023-11-25 13:39:54', '2023-11-25 13:39:54'),
	(8, 'Tai - Mũi - Họng', 'Khoa Tai - Mũi - Họng chuyên chẩn đoán và điều trị các vấn đề về tai, mũi, họng.', 'storage/image/thumbnail/departments/6561f92b65301.jpg', 8, '2023-11-25 13:39:55', '2023-11-25 13:39:55'),
	(9, 'Khoa Tiêu Hóa', 'Khoa Tiêu Hóa chăm sóc và điều trị các vấn đề về tiêu hóa và dạ dày.', 'storage/image/thumbnail/departments/6561f92bd0a90.jpg', 293, '2023-11-25 13:39:55', '2023-11-25 13:39:55'),
	(10, 'Tâm Lý', 'Khoa Tâm Lý cung cấp dịch vụ tư vấn tâm lý và chăm sóc tâm lý.', 'storage/image/thumbnail/departments/6561f92c5fbbe.jpg', 63, '2023-11-25 13:39:56', '2023-11-25 13:39:56'),
	(11, 'Vật Lý Trị Liệu', 'Khoa Vật Lý Trị Liệu chuyên cung cấp phục hồi chức năng và trị liệu vật lý.', 'storage/image/thumbnail/departments/6561f92cda9df.jpg', 193, '2023-11-25 13:39:56', '2023-11-25 13:39:56'),
	(12, 'Tâm Thần', 'Khoa Tâm Thần tập trung vào chăm sóc và điều trị các vấn đề tâm thần.', 'storage/image/thumbnail/departments/6561f92d524b6.jpg', 207, '2023-11-25 13:39:57', '2023-11-25 13:39:57'),
	(13, 'Trị Liệu Hô Hấp', 'Khoa Trị Liệu Hô Hấp chuyên trị liệu các bệnh về hô hấp.', 'storage/image/thumbnail/departments/6561f92dd0dfd.jpg', 39, '2023-11-25 13:39:57', '2023-11-25 13:39:57'),
